<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta http-equiv="X-UA-Compatible" content="IE=edge">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Document</title>
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.3.0/css/all.min.css">
    <link rel="stylesheet" href="../../../../Styles/Admin/admin.css">
</head>

<body>
    <section>
        <div class="container__tap__bar">
            <div class="__modol">
                Modern
            </div>
            <i class="fa-solid fa-bars"></i>
            <div class="__modol__icon">
                <div class="__icon">
                    <i class="fa-solid fa-magnifying-glass"></i>
                </div>
                <div class="__icon">
                    <i class="fa-solid fa-envelope"></i>
                </div>
                <div class="__icon">
                    <i class="fa-regular fa-bell"></i>
                </div>
                <div class="__icon __Image">
                    <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ13sRfxG087VWgojfOdJoHLwDYkEE8SsjYQWe23sVt&s"
                        alt="">
                </div>
                <div class="__icon">
                    <i class="fa-solid fa-right-from-bracket"></i>
                </div>
            </div>
        </div>
    </section>
    <section>
        <div class="container__menu_icon">
            <div class="__menu_grid_1">
                <div class="__menu_icon __Profile">
                    <img src="data:image/jpeg;base64,"
                        alt="">
                    <h5>Luc</h5>
                </div>
                <div class="__menu_icon __menu__ _menu_home__" onclick="Change_Page()">
                    <i class="fa-solid fa-house"></i>
                    <p>HOME</p>
                </div>
                <div class="__menu_icon __menu__ __menu_User__" onclick="Change_Page_User()">
                    <i class="fa-solid fa-users"></i>
                    <p>USERS</p>
                </div>
                <div class="__menu_icon __menu__ __Menu_comment__" onclick="Change_Page_Comment()">
                    <i class="fa-regular fa-comment"></i>
                    <p>COMMENT</p>
                </div>
                <div class="__menu_icon __menu__ __menu_course__" onclick="Change_Page_Course()">
                    <i class="fa-solid fa-book"></i>
                    <p>COURSES</p>
                </div>
                <div class="__menu_icon __menu__ __menu_Order__" onclick="Change_Page_Order()">
                    <i class="fa-sharp fa-solid fa-cart-shopping"></i>
                    <p>ORDERS</p>
                </div>
            </div>
            <div class="container__show_detail" >
                <div class="__detail_home_">
                    <p>HOME</p>
                </div>
                <div class="__quantity_four_div">
                    <div class="__four_div">
                        <i class="fa-solid fa-users"></i>
                        <p>User activity this month</p>
                    </div>
                    <div class="__four_div">
                        <i class="fa-regular fa-comment"></i>
                        <p>Quantity comment</p>
                    </div>
                    <div class="__four_div">
                        <i class="fa-solid fa-book"></i>
                        <p>Quantity course</p>
                    </div>
                    <div class="__four_div">
                        <i class="fa-sharp fa-solid fa-cart-shopping"></i>
                        <p>Quantity odered</p>
                    </div>
                </div>
            </div>
        </div>
    </section>
</body>
<script src="../../JS/Admin.js"></script>
<script>
    var Home = document.querySelector("._menu_home__");
    Home.style.background = "red";
</script>
</html>